import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [channelId, setChannelId] = useState('');
  const [streams, setStreams] = useState([]);

  const fetchLiveStreams = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/live-streams', {
        params: { channelId },
      });
      setStreams(response.data.items);
    } catch (error) {
      console.error('Error fetching live streams', error);
    }
  };

  useEffect(() => {
    if (channelId) {
      fetchLiveStreams();
    }
  }, [channelId]);

  return (
    <div className="App">
      <h1>YouTube Live Streams</h1>
      <input
        type="text"
        placeholder="Enter Channel ID"
        value={channelId}
        onChange={(e) => setChannelId(e.target.value)}
      />
      <button onClick={fetchLiveStreams}>Fetch Live Streams</button>
      <div className="streams">
        {streams.map((stream) => (
          <div key={stream.id.videoId} className="stream">
            <h3>{stream.snippet.title}</h3>
            <a href={`https://www.youtube.com/watch?v=${stream.id.videoId}`} target="_blank" rel="noopener noreferrer">
              Watch
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
